const e=JSON.parse('{"key":"v-6ad38918","path":"/performance/%E4%BC%98%E5%8C%96%E9%A6%96%E5%B1%8F%E4%BD%93%E9%AA%8CLazy-Load%E5%88%9D%E6%8E%A2.html","title":"优化首屏体验——Lazy-Load 初探","lang":"zh-CN","frontmatter":{"description":"优化首屏体验——Lazy-Load 初探 首先要告诉大家的是，截止到上个章节，我们需要大家绞尽脑汁去理解的“硬核”操作基本告一段落了。从本节开始，我们会一起去实现一些必知必会、同时难度不大的常用优化手段。 这部分内容不难，但很关键。尤其是近期有校招或跳槽需求的同学，还请务必对这部分内容多加留心，说不定下一次的面试题里就有它们的身影。 Lazy-Load 初相见 Lazy-Load，翻译过来是“懒加载”。它是针对图片加载时机的优化：在一些图片量比较大的网站（比如电商网站首页，或者团购网站、小游戏首页等），如果我们尝试在用户打开页面的时候，就把所有的图片资源加载完毕，那么很可能会造成白屏、卡顿等现象，因为图片真的太多了，一口气处理这么多任务，浏览器做不到啊！","head":[["meta",{"property":"og:url","content":"https://mister-hope.github.io/camps/performance/%E4%BC%98%E5%8C%96%E9%A6%96%E5%B1%8F%E4%BD%93%E9%AA%8CLazy-Load%E5%88%9D%E6%8E%A2.html"}],["meta",{"property":"og:site_name","content":"杜同学日记"}],["meta",{"property":"og:title","content":"优化首屏体验——Lazy-Load 初探"}],["meta",{"property":"og:description","content":"优化首屏体验——Lazy-Load 初探 首先要告诉大家的是，截止到上个章节，我们需要大家绞尽脑汁去理解的“硬核”操作基本告一段落了。从本节开始，我们会一起去实现一些必知必会、同时难度不大的常用优化手段。 这部分内容不难，但很关键。尤其是近期有校招或跳槽需求的同学，还请务必对这部分内容多加留心，说不定下一次的面试题里就有它们的身影。 Lazy-Load 初相见 Lazy-Load，翻译过来是“懒加载”。它是针对图片加载时机的优化：在一些图片量比较大的网站（比如电商网站首页，或者团购网站、小游戏首页等），如果我们尝试在用户打开页面的时候，就把所有的图片资源加载完毕，那么很可能会造成白屏、卡顿等现象，因为图片真的太多了，一口气处理这么多任务，浏览器做不到啊！"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-02-26T14:05:38.000Z"}],["meta",{"property":"article:author","content":"kangduu"}],["meta",{"property":"article:modified_time","content":"2025-02-26T14:05:38.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"优化首屏体验——Lazy-Load 初探\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-02-26T14:05:38.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"kangduu\\",\\"url\\":\\"https://github.com/kangduu\\"}]}"]]},"headers":[{"level":2,"title":"Lazy-Load 初相见","slug":"lazy-load-初相见","link":"#lazy-load-初相见","children":[]},{"level":2,"title":"一起写一个 Lazy-Load 吧！","slug":"一起写一个-lazy-load-吧","link":"#一起写一个-lazy-load-吧","children":[]},{"level":2,"title":"小结","slug":"小结","link":"#小结","children":[]}],"git":{"createdTime":1740578738000,"updatedTime":1740578738000,"contributors":[{"name":"kangduu","email":"dukang1127@163.com","commits":1}]},"readingTime":{"minutes":7.07,"words":2120},"filePathRelative":"performance/优化首屏体验Lazy-Load初探.md","localizedDate":"2025年2月26日","excerpt":"<h1> 优化首屏体验——Lazy-Load 初探</h1>\\n<p>首先要告诉大家的是，截止到上个章节，我们需要大家绞尽脑汁去理解的“硬核”操作基本告一段落了。从本节开始，我们会一起去实现一些必知必会、同时难度不大的常用优化手段。</p>\\n<p>这部分内容不难，但<strong>很关键</strong>。尤其是近期有校招或跳槽需求的同学，还请务必对这部分内容多加留心，说不定下一次的面试题里就有它们的身影。</p>\\n<h2> Lazy-Load 初相见</h2>\\n<p>Lazy-Load，翻译过来是“懒加载”。它是针对图片加载时机的优化：在一些图片量比较大的网站（比如电商网站首页，或者团购网站、小游戏首页等），如果我们尝试在用户打开页面的时候，就把所有的图片资源加载完毕，那么很可能会造成白屏、卡顿等现象，因为图片真的太多了，一口气处理这么多任务，浏览器做不到啊！</p>","autoDesc":true}');export{e as data};
