const e=JSON.parse('{"key":"v-18660ffc","path":"/performance/%E5%9B%BE%E7%89%87%E4%BC%98%E5%8C%96%E4%B9%8B%E8%B4%A8%E9%87%8F%E4%B8%8E%E6%80%A7%E8%83%BD%E7%9A%84%E5%8D%9A%E5%BC%88.html","title":"图片优化——质量与性能的博弈","lang":"zh-CN","frontmatter":{"description":"图片优化——质量与性能的博弈 《高性能网站建设指南》的作者 Steve Souders 曾在 2013 年的一篇 博客 中提到： 我的大部分性能优化工作都集中在 JavaScript 和 CSS 上，从早期的 Move Scripts to the Bottom 和 Put Stylesheets at the Top 规则。为了强调这些规则的重要性，我甚至说过，“JS 和 CSS 是页面上最重要的部分”。 几个月后，我意识到这是错误的。图片才是页面上最重要的部分。 我关注 JS 和 CSS 的重点也是如何能够更快地下载图片。图片是用户可以直观看到的。他们并不会关注 JS 和 CSS。确实，JS 和 CSS 会影响图片内容的展示，尤其是会影响图片的展示方式（比如图片轮播，CSS 背景图和媒体查询）。但是我认为 JS 和 CSS 只是展示图片的方式。在页面加载的过程中，应当先让图片和文字先展示，而不是试图保证 JS 和 CSS 更快下载完成。","head":[["meta",{"property":"og:url","content":"https://mister-hope.github.io/camps/performance/%E5%9B%BE%E7%89%87%E4%BC%98%E5%8C%96%E4%B9%8B%E8%B4%A8%E9%87%8F%E4%B8%8E%E6%80%A7%E8%83%BD%E7%9A%84%E5%8D%9A%E5%BC%88.html"}],["meta",{"property":"og:site_name","content":"杜同学日记"}],["meta",{"property":"og:title","content":"图片优化——质量与性能的博弈"}],["meta",{"property":"og:description","content":"图片优化——质量与性能的博弈 《高性能网站建设指南》的作者 Steve Souders 曾在 2013 年的一篇 博客 中提到： 我的大部分性能优化工作都集中在 JavaScript 和 CSS 上，从早期的 Move Scripts to the Bottom 和 Put Stylesheets at the Top 规则。为了强调这些规则的重要性，我甚至说过，“JS 和 CSS 是页面上最重要的部分”。 几个月后，我意识到这是错误的。图片才是页面上最重要的部分。 我关注 JS 和 CSS 的重点也是如何能够更快地下载图片。图片是用户可以直观看到的。他们并不会关注 JS 和 CSS。确实，JS 和 CSS 会影响图片内容的展示，尤其是会影响图片的展示方式（比如图片轮播，CSS 背景图和媒体查询）。但是我认为 JS 和 CSS 只是展示图片的方式。在页面加载的过程中，应当先让图片和文字先展示，而不是试图保证 JS 和 CSS 更快下载完成。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-02-26T14:05:38.000Z"}],["meta",{"property":"article:author","content":"kangduu"}],["meta",{"property":"article:modified_time","content":"2025-02-26T14:05:38.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"图片优化——质量与性能的博弈\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-02-26T14:05:38.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"kangduu\\",\\"url\\":\\"https://github.com/kangduu\\"}]}"]]},"headers":[{"level":2,"title":"2018 年，图片依然很大","slug":"_2018-年-图片依然很大","link":"#_2018-年-图片依然很大","children":[]},{"level":2,"title":"不同业务场景下的图片方案选型","slug":"不同业务场景下的图片方案选型","link":"#不同业务场景下的图片方案选型","children":[{"level":3,"title":"前置知识：二进制位数与色彩的关系","slug":"前置知识-二进制位数与色彩的关系","link":"#前置知识-二进制位数与色彩的关系","children":[]},{"level":3,"title":"JPEG/JPG","slug":"jpeg-jpg","link":"#jpeg-jpg","children":[]},{"level":3,"title":"PNG-8 与 PNG-24","slug":"png-8-与-png-24","link":"#png-8-与-png-24","children":[]},{"level":3,"title":"SVG","slug":"svg","link":"#svg","children":[]},{"level":3,"title":"Base64","slug":"base64","link":"#base64","children":[]},{"level":3,"title":"WebP","slug":"webp","link":"#webp","children":[]}]},{"level":2,"title":"小结","slug":"小结","link":"#小结","children":[]}],"git":{"createdTime":1740578738000,"updatedTime":1740578738000,"contributors":[{"name":"kangduu","email":"dukang1127@163.com","commits":1}]},"readingTime":{"minutes":18.53,"words":5559},"filePathRelative":"performance/图片优化之质量与性能的博弈.md","localizedDate":"2025年2月26日","excerpt":"<h1> 图片优化——质量与性能的博弈</h1>\\n<p>《高性能网站建设指南》的作者 Steve Souders 曾在 2013 年的一篇 <a href=\\"http://www.stevesouders.com/blog/2013/04/26/i/\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">博客</a> 中提到：</p>\\n<blockquote>\\n<p>我的大部分性能优化工作都集中在 JavaScript 和 CSS 上，从早期的 Move Scripts to the Bottom 和 Put Stylesheets at the Top 规则。为了强调这些规则的重要性，我甚至说过，“JS 和 CSS 是页面上最重要的部分”。</p>\\n<p>几个月后，我意识到这是错误的。图片才是页面上最重要的部分。</p>\\n<p>我关注 JS 和 CSS 的重点也是如何能够更快地下载图片。图片是用户可以直观看到的。他们并不会关注 JS 和 CSS。确实，JS 和 CSS 会影响图片内容的展示，尤其是会影响图片的展示方式（比如图片轮播，CSS 背景图和媒体查询）。但是我认为 JS 和 CSS 只是展示图片的方式。在页面加载的过程中，应当先让图片和文字先展示，而不是试图保证 JS 和 CSS 更快下载完成。</p>\\n</blockquote>","autoDesc":true}');export{e as data};
